// A simple service to handle audio playback for game events.

const sounds = {
  ding: 'data:audio/wav;base64,-AEAAA=',
  correct: 'data:audio/wav;base64,UklGRiQAAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YQAAAAA=',
  wrong: 'data:audio/wav;base64,',
};

// We create a single audio context and reuse audio elements to prevent memory leaks and improve performance.
const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
const audioElements: { [key: string]: HTMLAudioElement | null } = {
  ding: null,
  correct: null,
  wrong: null,
};

/**
 * Plays a sound by its key name.
 * It reuses Audio elements for better performance and to avoid memory leaks.
 * @param name The key of the sound to play.
 */
function playSound(name: keyof typeof sounds) {
  // Use a single AudioContext to prevent issues on some browsers.
  if (audioContext.state === 'suspended') {
    audioContext.resume();
  }
  
  // Create audio element on first play
  if (!audioElements[name]) {
    audioElements[name] = new Audio(sounds[name]);
  }

  const audio = audioElements[name];
  if (audio) {
    // By resetting the current time, we can play the sound again even if it's already playing.
    audio.currentTime = 0;
    audio.play().catch(error => {
      // Browser autoplay policies might block audio until a user interaction.
      // In this app, the first click should resolve this for subsequent plays.
      console.error(`Error playing sound "${name}":`, error);
    });
  }
}

export const playDingSound = () => playSound('ding');
export const playCorrectSound = () => playSound('correct');
export const playWrongSound = () => playSound('wrong');
